mapper.select:
SELECT DISTINCT brr.business_device_id as deviceId , brr.business_device_number as deviceNumber, brr.template_category as templateCategory , brr.business_contract_review_id as contractId , brr.template_id as templateId , brr.template_name as templateName , brr.row_id as reportId, brr.create_time as createTime , brr.is_print as isPrint, brr.is_start_flow as isStartFlow, brr.is_reset as isReset, brcr.contract_number as contractNumber , bd.receiving_user_name as receivingUserName , brr.file_name as fileName, brr.file_path as filePath, brr.file_upload_time as fileUploadTime, brr.file_pages as filePages, brr.file_relative_name as fileRelativeName FROM business_request_report brr LEFT JOIN business_device bd ON bd.row_id = brr.business_device_id LEFT JOIN business_relation_device_user brdu ON bd.row_id = brdu.device_id LEFT JOIN business_request_contract_review brcr ON brcr.row_id = bd.contract_id
brr.is_generic_report = '1' AND brdu.user_id = #{userId}
AND brr.template_category = #{templateCategory}
AND brcr.contract_number LIKE CONCAT('%' , #{queryParams.contractNumber} , '%')
AND bd.receiving_user_name LIKE CONCAT('%' , #{queryParams.receivingUserName} , '%')
ORDER BY brr.create_time DESC
